       ,a.update_by = #{updateBy, jdbcType=BIGINT}
    
 WHERE a.user_id = #{userId, jdbcType=BIGINT}

mapper.select:
SELECT a.user_id userId, a.user_name userName, a.real_name realName, a.password password, a.user_type userType, a.nick_name nickName, a.mobile_phone mobilePhone, a.email email, a.user_icon userIcon, a.status status, DATE_FORMAT(a.last_login_time, '%Y-%m-%d %T') lastLoginTime, a.login_times loginTimes, DATE_FORMAT(a.create_time, '%Y-%m-%d %T') createTime, a.create_by createBy, DATE_FORMAT(a.update_time, '%Y-%m-%d %T') updateTime, a.update_by updateBy FROM upms_user a WHERE 1=1
AND a.user_name like CONCAT('%', #{userName, jdbcType=VARCHAR}, '%')
AND a.real_name like CONCAT('%', #{realName, jdbcType=VARCHAR}, '%')
AND a.user_type = #{userType, jdbcType=TINYINT}
AND a.nick_name like CONCAT('%', #{nickName, jdbcType=VARCHAR}, '%')
AND a.mobile_phone = #{mobilePhone, jdbcType=VARCHAR}
AND a.email = #{email, jdbcType=VARCHAR}
AND a.status = #{status, jdbcType=TINYINT}

