mapper.update:
UPDATE sa_upms_resource a
   SET a.resource_id = #{resourceId, jdbcType=BIGINT}

       ,a.resource_name = #{resourceName, jdbcType=VARCHAR}


       ,a.resource_url = #{resourceUrl, jdbcType=VARCHAR}


       ,a.http_method = #{httpMethod, jdbcType=VARCHAR}


       ,a.action_type = #{actionType, jdbcType=TINYINT}


       ,a.siblings_index = #{siblingsIndex, jdbcType=INTEGER}


       ,a.resource_icon = #{resourceIcon, jdbcType=VARCHAR}


       ,a.index_page = #{indexPage, jdbcType=BOOLEAN}


       ,a.update_time = #{updateTime, jdbcType=VARCHAR}


       ,a.update_by = #{updateBy, jdbcType=BIGINT}

 WHERE a.resource_id = #{resourceId, jdbcType=BIGINT}

