   SET a.app_id = #{appId, jdbcType=BIGINT}
    
       ,a.app_name = #{appName, jdbcType=VARCHAR}
    
    
       ,a.description = #{description, jdbcType=VARCHAR}
    
    
       ,a.app_type = #{appType, jdbcType=TINYINT}
    
    
       ,a.app_key = #{appKey, jdbcType=VARCHAR}
    
    
       ,a.app_secret = #{appSecret, jdbcType=VARCHAR}
    
    
       ,a.app_web_context_path = #{appWebContextPath, jdbcType=VARCHAR}
    
    
       ,a.app_api_context_path = #{appApiContextPath, jdbcType=VARCHAR}
    
    
       ,a.enabled = #{enabled, jdbcType=BOOLEAN}
    
 WHERE a.app_id = #{appId, jdbcType=BIGINT}

