mapper.select:
SELECT a.app_id appId, a.app_name appName, a.description description, a.app_type appType, a.app_key appKey, a.app_secret appSecret, a.app_web_context_path appWebContextPath, a.app_api_context_path appApiContextPath, a.enabled enabled, DATE_FORMAT(a.create_time, '%Y-%m-%d %T') createTime, a.create_by createBy FROM upms_app a WHERE 1=1
AND a.app_name like CONCAT('%', #{appName, jdbcType=VARCHAR}, '%')
AND a.app_type = #{appType, jdbcType=TINYINT}
AND a.app_key = #{appKey, jdbcType=VARCHAR}
AND a.app_web_context_path = #{appWebContextPath, jdbcType=VARCHAR}
AND a.app_api_context_path = #{appApiContextPath, jdbcType=VARCHAR}
AND a.enabled = #{enabled, jdbcType=BOOLEAN}

