UPDATE upms_user_access_log a
   SET a.log_id = #{logId, jdbcType=BIGINT}
    
       ,a.log_title = #{logTitle, jdbcType=VARCHAR}
    
    
       ,a.app_module = #{appModule, jdbcType=VARCHAR}
    
    
       ,a.request_uri = #{requestUri, jdbcType=VARCHAR}
    
    
       ,a.http_method = #{httpMethod, jdbcType=VARCHAR}
    
    
       ,a.mvc_method = #{mvcMethod, jdbcType=VARCHAR}
    
    
       ,a.request_header = #{requestHeader, jdbcType=VARCHAR}
    
    
       ,a.request_content_type = #{requestContentType, jdbcType=VARCHAR}
    
    
       ,a.request_content = #{requestContent, jdbcType=VARCHAR}
    
    
       ,a.access_user_id = #{accessUserId, jdbcType=BIGINT}
    
    
       ,a.access_time = #{accessTime, jdbcType=VARCHAR}
    
    
       ,a.client_ip_addr = #{clientIpAddr, jdbcType=VARCHAR}
    
    
       ,a.server_ip_addr = #{serverIpAddr, jdbcType=VARCHAR}
    
    
       ,a.process_time = #{processTime, jdbcType=BIGINT}
    
    
       ,a.logging_completed = #{loggingCompleted, jdbcType=BOOLEAN}
    
    
       ,a.async_request = #{asyncRequest, jdbcType=BOOLEAN}
    
    
       ,a.response_content_type = #{responseContentType, jdbcType=VARCHAR}
    
    
       ,a.app_id = #{appId, jdbcType=BIGINT}
    
    
       ,a.response_content = #{responseContent, jdbcType=LONGVARCHAR}
    
 WHERE a.log_id = #{logId, jdbcType=BIGINT}

