SELECT a.log_id logId, a.log_title logTitle, a.app_module appModule, a.request_uri requestUri, a.http_method httpMethod, a.mvc_method mvcMethod, a.request_header requestHeader, a.request_content_type requestContentType, a.request_content requestContent, a.access_user_id accessUserId, DATE_FORMAT(a.access_time, '%Y-%m-%d %T') accessTime, a.client_ip_addr clientIpAddr, a.server_ip_addr serverIpAddr, a.process_time processTime, a.logging_completed loggingCompleted, a.async_request asyncRequest, a.response_content_type responseContentType, a.app_id appId, DATE_FORMAT(a.create_time, '%Y-%m-%d %T') createTime, a.response_content responseContent FROM upms_user_access_log a WHERE 1=1
AND a.log_title like CONCAT('%', #{logTitle, jdbcType=VARCHAR}, '%')
AND a.app_module = #{appModule, jdbcType=VARCHAR}
AND a.request_uri = #{requestUri, jdbcType=VARCHAR}
AND a.http_method = #{httpMethod, jdbcType=VARCHAR}
AND a.access_user_id = #{accessUserId, jdbcType=BIGINT}
AND a.logging_completed = #{loggingCompleted, jdbcType=BOOLEAN}
AND a.async_request = #{asyncRequest, jdbcType=BOOLEAN}
AND a.app_id = #{appId, jdbcType=BIGINT}

